svg.script:
	function createRequestObject() {
		var ro;
		var browser = navigator.appName;
		if (browser == "Microsoft Internet Explorer"){
		    ro = new ActiveXObject("Microsoft.XMLHTTP");
		}
		else {
		    ro = new XMLHttpRequest();
		}
		return ro;
	}

	var http = createRequestObject();

	function send() {
		try {
			http.open('get', "http://identi.ca/api/statuses/public_timeline.xml");
			//http.open('get', "../public_timeline.xml"); //workaround for Opera dev which has same site origin policy
			http.onreadystatechange = doSomething;
			http.send(null);
		}
		catch(e) {
			//alert(http.responseText);
		}
	}

	function doSomething() {
		if (http.readyState == 4) {
			var r = http.responseXML;
			var container = document.getElementById("canvas");
			var box = container.getBBox();

			var defs = document.getElementsByTagName("defs")[0];

			var animateElement = document.createElementNS(svgns, "animate");
			animateElement.setAttribute('id','shooter');
			animateElement.setAttribute('attributeName','y');
			animateElement.setAttribute('dur','10s');
			animateElement.setAttribute('values',box.y + box.height + ';' + box.y);
			animateElement.setAttribute('begin',0);
			defs.appendChild(animateElement);

			var statuses = r.documentElement.getElementsByTagName("status");
			for (status in statuses) {

				//alert(dent);
				var dent = statuses[status].getElementsByTagName("text")[0].firstChild.nodeValue;
				var myNewTextElement = addTextElement(container.parentNode,dent,{'x':box.x + box.width / 2,'y': box.y + box.height});

				var textAnimateElement = animateElement.cloneNode(true);
				textAnimateElement.setAttribute('begin',status);
/*
				var textAnimateElement = document.createElementNS(svgns, "animate");
				textAnimateElement.setAttribute('attributeName','y');
				textAnimateElement.setAttribute('dur','10s');
				textAnimateElement.setAttribute('values',box.y + box.height + ';' + box.y);
				textAnimateElement.setAttribute('begin',status);
*/
				myNewTextElement.appendChild(textAnimateElement);

				//container.parentNode.appendChild(myNewTextElement);

				var avatar = statuses[status].getElementsByTagName("profile_image_url")[0].firstChild.nodeValue;
				//alert(avatar);
				var myNewImageElement = addImageElement(container.parentNode,avatar,{'x':box.x + box.width / 2,'y': box.y + box.height,'width':64,'height':64});
				var imageAnimateElement = textAnimateElement.cloneNode(true);
				imageAnimateElement.setAttribute('begin',status);
				myNewImageElement.appendChild(imageAnimateElement);
			}
		}
	}
